const initialState = {
    P1: [],
    P2: []
}

function updatePlayerDataReducer (state=initialState, action) {
    if (action.type === "PlayerProgress/updatePlayerData") {
        const player = action.payload.player
        // console.log(player);
        //let playerData = [...state[player]];
        //console.log(state)
        let jj;
        if(player === "P1"){
            jj = {
                ...state,
                P1: [...(state.P1), action.payload.element]
            }
        } else {
            jj = {
                ...state,
                P2: action.payload.element
            }
        }
        return jj
    }
    else {
        return state
    }
}

export default updatePlayerDataReducer;
